results = ""
FizzBuzz(1)
console.log(`results = ${results}`)

function FizzBuzz(s) {

    if (s > 100) {
        return
    }
    for (let i = 0; i <= 100; i++) {
        if (i == s) {
            stri = i.toString()
            div5 = false
            div3 = false
            if (stri.charAt(stri.length - 1) == '5' || stri.charAt(stri.length - 1) == '0') {
                div5 = true
            }

            gotSum = false
            while (!gotSum) {
                sum = 0
                for (let l = 0; l < stri.length; l++) {
                    v = parseInt(stri[l])
                    sum = sum + v
                }
                sumS = sum.toString()
                if (sumS.length >= 2) {
                    stri = sumS
                } else {
                    if (sum == 3 || sum == 6 || sum == 9) {
                        div3 = true
                    }
                    gotSum = true
                }
            }
            if (div3) {
                results = results.concat(`Fizz`)
            }
            if (div5) {
                results = results.concat(`Buzz`)
            }  
            if (!div3 && !div5) {
                results = results.concat(`${i}`)
            }
            if(i<100) {
                results = results.concat(`,`)
            }
        }
    }
    FizzBuzz(s + 1)
}
